const payload = $input.first().json.body || $input.first().json;

if (!payload.tenant_id) {
  throw new Error('Missing required field: tenant_id');
}

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
if (!uuidRegex.test(payload.tenant_id)) {
  throw new Error('Invalid tenant_id format');
}

// max_weeks lets callers limit how far back to go (default 9 = ~65 days, Amazon's limit)
const maxWeeks = parseInt(payload.max_weeks || '9');

return [{
  json: {
    tenant_id: payload.tenant_id,
    max_weeks: Math.min(Math.max(maxWeeks, 1), 9),
    trigger_source: payload.trigger_source || 'manual',
    triggered_at: payload.timestamp || new Date().toISOString()
  }
}];

// Pull tenant_id and max_weeks from upstream
const tenantId = $('Extract Tenant ID').first().json.tenant_id;
const maxWeeks = $('Extract Tenant ID').first().json.max_weeks || 9;

// Generate offsets from oldest to newest so we build history in order
// offset 9 = 9 weeks ago, offset 1 = last week
const items = [];
for (let offset = maxWeeks; offset >= 1; offset--) {
  items.push({
    json: {
      week_offset: offset,
      tenant_id: tenantId
    }
  });
}

return items;

const weekOffset = $input.first().json.week_offset;
const tenantId = $input.first().json.tenant_id;

function getISOWeek(d) {
  const date = new Date(d.getTime());
  date.setHours(0, 0, 0, 0);
  date.setDate(date.getDate() + 3 - (date.getDay() + 6) % 7);
  const week1 = new Date(date.getFullYear(), 0, 4);
  return 1 + Math.ceil(((date.getTime() - week1.getTime()) / 86400000 - 3 + (week1.getDay() + 6) % 7) / 7);
}

const msPerDay = 24 * 60 * 60 * 1000;
const offsetDays = weekOffset * 7;

// Shift 'now' back by the week offset to get the reference point for this week
const shiftedNow = new Date(Date.now() - offsetDays * msPerDay);

// Date ranges — same formula as Flow 1 but with the week offset applied
const endDate      = new Date(Date.now() - (3  + offsetDays) * msPerDay).toISOString().split('T')[0];
const startDate30  = new Date(Date.now() - (32 + offsetDays) * msPerDay).toISOString().split('T')[0];
const startDate7   = new Date(Date.now() - (9  + offsetDays) * msPerDay).toISOString().split('T')[0];
const yesterday    = new Date(Date.now() - (1  + offsetDays) * msPerDay).toISOString().split('T')[0];
const dayBefore    = new Date(Date.now() - (2  + offsetDays) * msPerDay).toISOString().split('T')[0];
const today        = shiftedNow.toISOString().split('T')[0];

// ISO week ID — same logic as Flow 1's Calculate Week ID node
const day  = shiftedNow.getDay();
const diff = shiftedNow.getDate() - day + (day === 0 ? -6 : 1);
const monday = new Date(shiftedNow);
monday.setDate(diff);
monday.setHours(0, 0, 0, 0);
const sunday = new Date(monday);
sunday.setDate(sunday.getDate() + 6);

const weekNum = getISOWeek(monday);
const year = monday.getFullYear();

return [{
  json: {
    week_offset:  weekOffset,
    tenant_id:    tenantId,
    week_id:      `${year}-W${String(weekNum).padStart(2, '0')}`,
    year:         year,
    week_number:  weekNum,
    start_date:   monday.toISOString().split('T')[0],
    end_date:     sunday.toISOString().split('T')[0],
    endDate:      endDate,
    startDate30:  startDate30,
    startDate7:   startDate7,
    yesterday:    yesterday,
    dayBefore:    dayBefore,
    today:        today
  }
}];
